var addSorting = (function() {
    'use strict';
    var cols,
        currentSort = {
            index: 0,
            desc: false
        };

    // returns the summary table element
    function getTable() {
        return document.querySelector('.coverage-summary');
    }
    // returns the thead element of the summary table
    function getTableHeader() {
        return getTable().querySelector('thead tr');
    }
    // returns the tbody element of the summary table
    function getTableBody() {
        return getTable().querySelector('tbody');
    }
    // returns the th element for nth column
    function getNthColumn(n) {
        return getTableHeader().querySelectorAll('th')[n];
    }

    function onFilterInput() {
        const searchValue = document.getElementById('fileSearch').value;
        const rows = document.getElementsByTagName('tbody')[0].children;

        // Try to create a RegExp from the searchValue. If it fails (invalid regex),
        // it will be treated as a plain text search
        let searchRegex;
        try {
            searchRegex = new RegExp(searchValue, 'i'); // 'i' for case-insensitive
        } catch (error) {
            searchRegex = null;
        }

        for (let i = 0; i < rows.length; i++) {
            const row = rows[i];
            let isMatch = false;

            if (searchRegex) {
                // If a valid regex was created, use it for matching
                isMatch = searchRegex.test(row.textContent);
            } else {
                // Otherwise, fall back to the original plain text search
                isMatch = row.textContent
                    .toLowerCase()
                    .includes(searchValue.toLowerCase());
            }

            row.style.display = isMatch ? '' : 'none';
        }
    }

    // loads the search box
    function addSearchBox() {
        var template = document.getElementById('filterTemplate');
        var templateClone = template.content.cloneNode(true);
        templateClone.getElementById('fileSearch').oninput = onFilterInput;
        template.parentElement.appendChild(templateClone);
    }

    // loads all columns
    function loadColumns() {
        var colNodes = getTableHeader().querySelectorAll('th'),
            colNode,
            cols = [],
            col,
            i;

        for (i = 0; i < colNodes.length; i += 1) {
            colNode = colNodes[i];
            col = {
                key: colNode.getAttribute('data-col'),
                sortable: !colNode.getAttribute('data-nosort'),
                type: colNode.getAttribute('data-type') || 'string'
            };
            cols.push(col);
            if (col.sortable) {
                col.defaultDescSort = col.type === 'number';
                colNode.innerHTML =
                    colNode.innerHTML + '<span class="sorter"></span>';
            }
        }
        return cols;
    }
    // attaches a data attribute to every tr element with an object
    // of data values keyed by column name
    function loadRowData(tableRow) {
        var tableCols = tableRow.querySelectorAll('td'),
            colNode,
            col,
            data = {},
            i,
            val;
        for (i = 0; i < tableCols.length; i += 1) {
            colNode = tableCols[i];
            col = cols[i];
            val = colNode.getAttribute('data-value');
            if (col.type === 'number') {
                val = Number(val);
            }
            data[col.key] = val;
        }
        return data;
    }
    // loads all row data
    function loadData() {
        var rows = getTableBody().querySelectorAll('tr'),
            i;

        for (i = 0; i < rows.length; i += 1) {
            rows[i].data = loadRowData(rows[i]);
        }
    }
    // sorts the table using the data for the ith column
    function sortByIndex(index, desc) {
        var key = cols[index].key,
            sorter = function(a, b) {
                a = a.data[key];
                b = b.data[key];
                return a < b ? -1 : a > b ? 1 : 0;
            },
            finalSorter = sorter,
            tableBody = document.querySelector('.coverage-summary tbody'),
            rowNodes = tableBody.querySelectorAll('tr'),
            rows = [],
            i;

        if (desc) {
            finalSorter = function(a, b) {
                return -1 * sorter(a, b);
            };
        }

        for (i = 0; i < rowNodes.length; i += 1) {
            rows.push(rowNodes[i]);
            tableBody.removeChild(rowNodes[i]);
        }

        rows.sort(finalSorter);

        for (i = 0; i < rows.length; i += 1) {
            tableBody.appendChild(rows[i]);
        }
    }
    // removes sort indicators for current column being sorted
    function removeSortIndicators() {
        var col = getNthColumn(currentSort.index),
            cls = col.className;

        cls = cls.replace(/ sorted$/, '').replace(/ sorted-desc$/, '');
        col.className = cls;
    }
    // adds sort indicators for current column being sorted
    function addSortIndicators() {
        getNthColumn(currentSort.index).className += currentSort.desc
            ? ' sorted-desc'
            : ' sorted';
    }
    // adds event listeners for all sorter widgets
    function enableUI() {
        var i,
            el,
            ithSorter = function ithSorter(i) {
                var col = cols[i];

                return function() {
                    var desc = col.defaultDescSort;

                    if (currentSort.index === i) {
                        desc = !currentSort.desc;
                    }
                    sortByIndex(i, desc);
                    removeSortIndicators();
                    currentSort.index = i;
                    currentSort.desc = desc;
                    addSortIndicators();
                };
            };
        for (i = 0; i < cols.length; i += 1) {
            if (cols[i].sortable) {
                // add the click event handler on the th so users
                // dont have to click on those tiny arrows
                el = getNthColumn(i).querySelector('.sorter').parentElement;
                if (el.addEventListener) {
                    el.addEventListener('click', ithSorter(i));
                } else {
                    el.attachEvent('onclick', ithSorter(i));
                }
            }
        }
    }
    // adds sorting functionality to the UI
    return function() {
        if (!getTable()) {
            return;
        }
        cols = loadColumns();
        loadData();
        addSearchBox();
        addSortIndicators();
        enableUI();
    };
})();

window.addEventListener('load', addSorting);

import {
  Controller,
  Get,
  Post,
  Body,
  Put,
  Param,
  Delete,
  UsePipes,
  UseGuards,
} from '@nestjs/common';
import { ProductService } from './product.service';
import { ProductDto } from './dto/product.dto';
import { CreateProductDto } from './dto/create-product.dto';
import { UpdateProductDto } from './dto/update-product.dto';
import { ApiOkResponse } from '@nestjs/swagger';
import { ValidateProductPipe } from './pipes/validate-product.pipe';
import { ValidateProductUpdatePipe } from './pipes/validate-productUpdate.pipe';
import { NormalizePipe } from '../common/pipes/normalize.nombre.pipe';
import { AuthGuard, Roles } from '@thallesp/nestjs-better-auth';
import { Role } from '@prisma/client';

@UseGuards(AuthGuard)
@Controller('product')
export class ProductController {
  constructor(private readonly productService: ProductService) {}

  @ApiOkResponse({
    type: ProductDto,
    description: 'Producto creado exitosamente',
  })
  @Roles([Role.ADMIN])
  @Post()
  @UsePipes(ValidateProductPipe, NormalizePipe)
  create(@Body() createProductDto: CreateProductDto) {
    return this.productService.create(createProductDto);
  }

  @ApiOkResponse({
    type: ProductDto,
    isArray: true,
    description: 'Productos obtenidos exitosamente',
  })
  @Get()
  findAll() {
    return this.productService.findAll();
  }

  @ApiOkResponse({
    type: ProductDto,
    description: 'Producto obtenido exitosamente',
  })
  @Get(':id')
  findOne(@Param('id') id: string) {
    return this.productService.findOne(id);
  }

  @ApiOkResponse({
    type: ProductDto,
    description: 'Producto actualizado exitosamente',
  })
  @Roles([Role.ADMIN])
  @Put(':id')
  @UsePipes(NormalizePipe)
  update(
    @Param('id') id: string,
    @Body(ValidateProductUpdatePipe) updateProductDto: UpdateProductDto,
  ) {
    return this.productService.update(id, updateProductDto);
  }
  @ApiOkResponse({
    description: 'Producto eliminado exitosamente',
  })
  @Roles([Role.ADMIN])
  @Delete(':id')
  softDelete(@Param('id') id: string) {
    return this.productService.softDelete(id);
  }
}

// src/cliente/pipe/normalize-cuil.pipe.spec.ts
import { ValidateCuilPipe } from './normalize-cuil.pipe';
import { BadRequestException } from '@nestjs/common';

describe('ValidateCuilPipe', () => {
  let pipe: ValidateCuilPipe;

  beforeEach(() => {
    pipe = new ValidateCuilPipe();
  });

  it('should be defined', () => {
    expect(pipe).toBeDefined();
  });

  describe('transform', () => {
    it('should normalize a valid CUIL with spaces', () => {
      const result = pipe.transform('20 34567890 1');
      expect(result).toBe('20345678901');
    });

    it('should accept a valid CUIL without spaces', () => {
      const result = pipe.transform('20345678901');
      expect(result).toBe('20345678901');
    });

    it('should throw BadRequestException for non-string input', () => {
      expect(() => pipe.transform(123 as any)).toThrow(BadRequestException);
      expect(() => pipe.transform(123 as any)).toThrow('El CUIL debe ser un string.');
    });

    it('should throw BadRequestException for invalid characters', () => {
      expect(() => pipe.transform('20a345678901')).toThrow(BadRequestException);
      expect(() => pipe.transform('20a345678901')).toThrow(
        'El CUIL contiene caracteres inválidos: "a". Solo se permiten números y espacios.',
      );
    });

    it('should throw BadRequestException for invalid format', () => {
      expect(() => pipe.transform('12345678901')).toThrow(BadRequestException);
      expect(() => pipe.transform('12345678901')).toThrow(
        'El CUIL "12345678901" no es válido. Debe tener 11 dígitos y comenzar con 20, 23, 24, 27, 30, 33 o 34.',
      );
    });

    it('should throw BadRequestException for wrong length', () => {
      expect(() => pipe.transform('2034567890')).toThrow(BadRequestException);
      expect(() => pipe.transform('203456789012')).toThrow(BadRequestException);
    });
  });
});

// src/cliente/pipe/normalize-email.pipe.spec.ts
import { ValidateEmailPipe } from './normalize-email.pipe';
import { BadRequestException } from '@nestjs/common';

describe('ValidateEmailPipe', () => {
  let pipe: ValidateEmailPipe;

  beforeEach(() => {
    pipe = new ValidateEmailPipe();
  });

  it('should be defined', () => {
    expect(pipe).toBeDefined();
  });

  describe('transform', () => {
    it('should normalize a valid email', () => {
      const result = pipe.transform('  User@Example.COM  ');
      expect(result).toBe('user@example.com');
    });

    it('should throw BadRequestException for non-string input', () => {
      expect(() => pipe.transform(123 as any)).toThrow(BadRequestException);
      expect(() => pipe.transform(123 as any)).toThrow('El email debe ser un string.');
    });

    it('should throw BadRequestException for empty email', () => {
      expect(() => pipe.transform('   ')).toThrow(BadRequestException);
      expect(() => pipe.transform('   ')).toThrow('El email no puede estar vacío.');
    });

    it('should throw BadRequestException for too long email', () => {
      const longLocal = 'a'.repeat(65) + '@example.com';
      expect(() => pipe.transform(longLocal)).toThrow(BadRequestException);
      expect(() => pipe.transform(longLocal)).toThrow(
        'La parte local del email (antes de @) es demasiado larga.',
      );
    });

    it('should throw BadRequestException for invalid local part', () => {
      expect(() => pipe.transform('user@exam#ple.com')).toThrow(BadRequestException);
      expect(() => pipe.transform('user@exam#ple.com')).toThrow(
        'La parte local del email contiene caracteres inválidos.',
      );
    });

    it('should throw BadRequestException for invalid domain', () => {
      expect(() => pipe.transform('user@exam_ple')).toThrow(BadRequestException);
      expect(() => pipe.transform('user@exam_ple')).toThrow(
        'El dominio del email no es válido. Debe contener al menos un punto y solo caracteres válidos.',
      );
    });

    it('should throw BadRequestException for missing @', () => {
      expect(() => pipe.transform('userexample')).toThrow(BadRequestException);
      expect(() => pipe.transform('userexample')).toThrow(
        'El email debe contener un solo "@" y partes local y dominio válidas.',
      );
    });
  });
});

// src/cliente/pipe/normalize-telefono.pipe.spec.ts
import { ValidateTelefonoPipe } from './normalize-telefono.pipe';
import { BadRequestException } from '@nestjs/common';

describe('ValidateTelefonoPipe', () => {
  let pipe: ValidateTelefonoPipe;

  beforeEach(() => {
    pipe = new ValidateTelefonoPipe();
  });

  it('should be defined', () => {
    expect(pipe).toBeDefined();
  });

  describe('transform', () => {
    it('should normalize a valid phone number with spaces', () => {
      const result = pipe.transform('  54 911 1234 5678  ');
      expect(result).toBe('5491112345678');
    });

    it('should accept a valid phone number with +', () => {
      const result = pipe.transform('+54 911 12345678');
      expect(result).toBe('+5491112345678');
    });

    it('should throw BadRequestException for non-string input', () => {
      expect(() => pipe.transform(123 as any)).toThrow(BadRequestException);
      expect(() => pipe.transform(123 as any)).toThrow('El teléfono debe ser un string.');
    });

    it('should throw BadRequestException for invalid characters', () => {
      expect(() => pipe.transform('54a91112345678')).toThrow(BadRequestException);
      expect(() => pipe.transform('54a91112345678')).toThrow(
        'El teléfono contiene caracteres inválidos: "a". Solo se permiten números y el símbolo "+".',
      );
    });

    it('should throw BadRequestException for too few digits', () => {
      expect(() => pipe.transform('123456789')).toThrow(BadRequestException);
      expect(() => pipe.transform('123456789')).toThrow(
        'El teléfono debe tener al menos 10 dígitos numéricos.',
      );
    });

    it('should throw BadRequestException for empty phone', () => {
      expect(() => pipe.transform('   ')).toThrow(BadRequestException);
      expect(() => pipe.transform('   ')).toThrow('El teléfono no puede estar vacío.');
    });
  });
});

import { BadRequestException } from '@nestjs/common';
import { checkUniqueName } from './check.nombre.helper';
import { PrismaService } from 'src/prisma/prisma.service';

describe('checkUniqueName helper', () => {
  let prisma: PrismaService;

  beforeEach(() => {
    prisma = {
      marca: { findFirst: jest.fn() },
      linea: { findFirst: jest.fn() },
    } as any;
  });

  it('debe resolver si no existe ninguna marca con ese nombre', async () => {
    (prisma.marca.findFirst as jest.Mock).mockResolvedValue(null);

    await expect(checkUniqueName(prisma, 'marca', 'NuevaMarca')).resolves.toBeUndefined();

    expect(prisma.marca.findFirst as jest.Mock).toHaveBeenCalledWith({
      where: { nombre: 'nuevamarca' },
    });
  });

  it('debe lanzar BadRequestException si ya existe la marca', async () => {
    (prisma.marca.findFirst as jest.Mock).mockResolvedValue({
      id: '1',
      nombre: 'marcaexistente',
    });

    await expect(checkUniqueName(prisma, 'marca', 'MarcaExistente')).rejects.toThrow(
      BadRequestException,
    );

    expect(prisma.marca.findFirst).toHaveBeenCalledWith({
      where: { nombre: 'marcaexistente' },
